print(1+1) # 2
print(3-2) # 1
print(5*2) # 10
print(6/3) # 2

print(2**3) # 2^3 + 8, 2의 세제곱
print(5%3) # 나머지 2
print(5//3) # 5를 3으로 나누었을때 몫
print(10//3) # 10을 3으로 나누었을때 몫

print(10 > 3) # True
print(4 >= 7) # False
print(10 < 3) # False
print(5 <= 5) # True

print(3 == 3) # 앞의 값과 뒤의 값이 똑같다?
print(4 == 2) # False
print(3 + 4 == 7) # True
